					alert("验证码输入错误");
					return false;
				}else if(data == 1){
					window.location.href="/binding/bingdingindex"; 
				}else if(data == 2){
					window.location.href="/agent/index"; 
				}else if(data == 4){
					alert("帐号已过期,请续费！");
					return false;
				}else if(data == 7){
					alert("帐号或密码有误！");	//您的版本无权登陆平台
					return false;
				}else if(data.code == 6){
					window.location.href = data.userhost+"/index/synchronouslogin?data="+data.userdata; 
					return false;
				}else{
					alert("帐号或密码有误！");
					return false;
				}
			}
		});
